mapper.select:
SELECT delivery_id, order_id, delivery_date, employee_num, delivery_status, remark, company_num, -- bo.order_code, order_name, branch_office_id, order_date, due_date FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY TO_NUMBER(REGEXP_SUBSTR(bd.delivery_id, '\d+')) desc ) AS RN, bd.delivery_id, bd.order_id, bd.delivery_date, bd.employee_num, bd.delivery_status, bd.remark, bd.company_num, -- bo.order_code, bo.order_name, bo.branch_office_id, bo.order_date, bo.due_date
FROM bsn_delivery bd LEFT JOIN bsn_order bo ON bo.order_id = bd.order_id WHERE bd.company_num = #{companyNum} AND bd.delivery_status = 'DS01'
) WHERE RN BETWEEN #{start} AND #{end}

SELECT delivery_lot_detail_num, delivery_detail_id, goods_lot_num, delivery_possible_qnt, delivery_qnt, goods_consumption_date FROM ( SELECT /*+ INDEX_ASC(bsn_delivery_lot_detail PK_BSN_DELIVERY_LOT_DETAIL) */ ROWNUM rn, bdl.delivery_lot_detail_num, bdl.delivery_detail_id, bdl.goods_lot_num, -- 여기서 delivery_possible_qnt를 계산 TRUNC((pwb.warehousing_remaining_quantity) - COALESCE(( SELECT SUM(delivery_qnt) FROM bsn_delivery_lot_detail bdl2 JOIN bsn_delivery_detail bdd2 ON bdl2.delivery_detail_id = bdd2.delivery_detail_id JOIN bsn_delivery bd2 ON bdd2.delivery_id = bd2.delivery_id WHERE bdl2.goods_lot_num = bdl.goods_lot_num AND bd2.delivery_status = 'DS01'
AND bdl2.delivery_detail_id < #{deliveryDetailId} )* goods_standard_ratio, 0))/ goods_standard_ratio AS delivery_possible_qnt,
bdl.delivery_qnt, pwb.goods_consumption_date FROM bsn_delivery_lot_detail bdl LEFT JOIN purchs_warehousing_body pwb ON bdl.goods_lot_num = pwb.goods_lot_num LEFT JOIN bsn_delivery_detail bdd ON bdl.delivery_detail_id = bdd.delivery_detail_id LEFT JOIN bsn_order_detail bod ON bdd.order_detail_id = bod.order_detail_id LEFT JOIN ( SELECT order_detail_id, REGEXP_SUBSTR(goods_standard, '\d+', 2, 1) / REGEXP_SUBSTR(goods_standard, '\d+', 1, 1) AS goods_standard_ratio FROM bsn_order_detail ) ratio_table ON bod.order_detail_id = ratio_table.order_detail_id WHERE bdl.delivery_detail_id = #{deliveryDetailId} ) WHERE RN BETWEEN #{start} AND #{end}
