mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT c.COMPANY_NUM, c.COMPANY_NAME, c.COMPANY_ENG_NAME, c.REPRESENTATION_NAME, c.REPRESENTATION_PHONE, c.CHARGER_NAME, c.CHARGER_PHONE, c.CHARGER_EMAIL, c.COMPANY_ADDRESS, c.BUSINESS_NUM, c.BUSINESS_LICENSE, c.SERVICE_STATE, c.REGISTER_DATE, d.contract_num, l.subscription_num, l.subscription_end_date, l.subscription_form from erp_company c left join erp_contract_document d on c.company_num = d.company_num left join erp_subscription_info_list l on c.company_num = l.company_num and l.subscription_name_num in (1, 6)
order by c.COMPANY_NUM DESC ) A
WHERE ROWNUM <= #{end}
)
WHERE RN >= #{start}

mapper.delete:
	delete from erp_subscription_info_list
    WHERE company_num = #{companyNum}
    AND SUBSCRIPTION_NAME_NUM NOT IN (1, 6)

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( select inquiry_num, company_name, charger_name, charger_phone, charger_email, inquiry_content, inquiry_date, ( select count(*) from erp_answer where inquiry_num = i.inquiry_num ) as cnt_inquiry from erp_inquiry i
order by i.inquiry_date DESC ) A
WHERE ROWNUM <= #{end}
)
WHERE RN >= #{start}
