SELECT * FROM ( SELECT ROWNUM rn, employeeNum, employeeId, employeeName, email, phone, hireDate, position, status, employmentType, departmentName, contractStatus FROM ( SELECT e.employee_num AS employeeNum, e.employee_id AS employeeId, e.employee_name AS employeeName, e.email AS email, e.phone AS phone, e.hire_date AS hireDate, fn_get_cmmn_name(e.position) AS position, fn_get_cmmn_name(e.status) AS status, fn_get_cmmn_name(e.employment_type) AS employmentType, COALESCE(d.department_name, 'Î∂ÄÏÑú ÏóÜÏùå') AS departmentName,
CASE WHEN c.contract_num IS NOT NULL THEN 'Î≥¥Í∏∞' ELSE 'ÎØ∏Í≥ÑÏïΩ' END AS contractStatus
FROM hr_employee e LEFT JOIN hr_department d ON e.department_num = d.department_num LEFT JOIN ( SELECT contract_num, employee_num FROM ( SELECT contract_num, employee_num, ROW_NUMBER() OVER (PARTITION BY employee_num ORDER BY contract_start_date DESC) AS rn FROM hr_employee_contract ) WHERE rn = 1 ) c ON e.employee_num = c.employee_num
WHERE e.company_num = #{companyNum}
AND e.department_num IN ( SELECT department_num FROM hr_department WHERE company_num = #{companyNum} START WITH department_num = #{departmentNum} CONNECT BY PRIOR department_num = parent_department_num )
ORDER BY e.employee_num DESC )
WHERE ROWNUM <= #{end}
) WHERE rn >= #{start}

