SELECT * FROM ( SELECT ROWNUM rn, a.* FROM ( SELECT e.employee_num, e.employee_id, e.employee_name, e.email , e.phone , e.hire_date, fn_get_cmmn_name(e.position) as position, fn_get_cmmn_name(e.authority) as authority, fn_get_cmmn_name(e.status) as status, e.quit_date, e.address, e.zip_code, fn_get_cmmn_name(e.employment_type) as employmentType, e.profile_image, e.memo, e.company_num, c.company_name, c.company_eng_name, e.department_num, d.department_name, d.parent_department_num, d.department_type, d.manager_num, d.department_status FROM hr_employee e LEFT JOIN hr_department d ON e.department_num = d.department_num LEFT JOIN erp_company c ON e.company_num = c.company_num WHERE e.company_num = #{companyNum}
AND (e.department_num IN ( -- ‚úÖ Ìï¥Îãπ Î∂ÄÏÑúÏôÄ Î™®Îì† ÌïòÏúÑ Î∂ÄÏÑúÎ•º Ìè¨Ìï®ÌïòÎèÑÎ°ù ÏàòÏ†ï SELECT department_num FROM hr_department WHERE company_num = #{companyNum} START WITH department_num = #{departmentNum} CONNECT BY PRIOR department_num = parent_department_num ))
ORDER BY e.employee_num DESC ) a
WHERE ROWNUM <= #{end}
) WHERE rn >= #{start}

SELECT * 
FROM (
    SELECT a.*, ROWNUM rn 
    FROM (
        SELECT e.employee_num AS employeeNum,
               e.employee_id  AS employeeId,
               e.employee_name AS employeeName,
               e.email,
               fn_get_cmmn_name(e.position) AS position, 
               d.department_name AS departmentName,
               e.department_num AS departmentNum,
               COUNT(*) OVER() AS totalEmployeeCount
        FROM hr_employee e
        JOIN hr_department d ON e.department_num = d.department_num



                AND e.company_num = #{companyNum}



                AND (
                    e.department_num = #{departmentNum}
                    OR e.department_num IN (
                        SELECT department_num
                        FROM hr_department
                        WHERE parent_department_num = #{departmentNum}
                    )
                )


        ORDER BY e.employee_num DESC 
    ) a 

    WHERE ROWNUM <= #{end}

) 
WHERE rn >= #{start}

