mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, employeeNum, employeeId, employeeName, email, phone, hireDate, position, status, employmentType, departmentName, authority FROM ( SELECT e.employee_num AS employeeNum, e.employee_id AS employeeId, e.employee_name AS employeeName, e.email AS email, e.phone AS phone, e.hire_date AS hireDate, fn_get_cmmn_name(e.position) as position, fn_get_cmmn_name(e.status) as status, fn_get_cmmn_name(e.authority) as authority, fn_get_cmmn_name(e.employment_type) as employmentType, d.department_name AS departmentName FROM hr_employee e LEFT JOIN hr_department d ON e.department_num = d.department_num
AND e.company_num = #{companyNum}
AND e.department_num = #{departmentNum}
)
WHERE ROWNUM <= #{end}
) WHERE rn >= #{start}

SELECT COUNT(DISTINCT e.employee_num)  
FROM hr_employee e
LEFT JOIN hr_department d ON e.department_num = d.department_num
WHERE e.company_num = #{companyNum}
AND (
    e.department_num IN (
        SELECT DISTINCT department_num FROM hr_department
        WHERE company_num = #{companyNum}
        START WITH department_num = #{departmentNum}  
        CONNECT BY PRIOR department_num = parent_department_num  
    )
    OR e.department_num IS NULL 
)

SELECT * FROM ( SELECT ROWNUM rn, employeeNum, employeeId, employeeName, email, phone, hireDate, position, positionCode, status, employmentType, employmentTypeCode, departmentNum, departmentName, parentDepartmentNum, parentDepartmentName, companyNum, companyName FROM ( SELECT e.employee_num AS employeeNum, e.employee_id AS employeeId, e.employee_name AS employeeName, e.email AS email, e.phone AS phone, e.hire_date AS hireDate, fn_get_cmmn_name(e.position) AS position, e.position AS positionCode, fn_get_cmmn_name(e.status) AS status, fn_get_cmmn_name(e.employment_type) AS employmentType, e.employment_type AS employmentTypeCode, d.department_num AS departmentNum, COALESCE(d.department_name, 'Î∂ÄÏÑú ÏóÜÏùå') AS departmentName, d.parent_department_num AS parentDepartmentNum, p.department_name AS parentDepartmentName, c.company_num AS companyNum, c.company_name AS companyName FROM hr_employee e LEFT JOIN hr_department d ON e.department_num = d.department_num LEFT JOIN hr_department p ON d.parent_department_num = p.department_num LEFT JOIN erp_company c ON e.company_num = c.company_num WHERE e.company_num = #{companyNum} AND e.department_num IN ( SELECT department_num FROM hr_department WHERE company_num = #{companyNum}
START WITH department_num = #{departmentNum} CONNECT BY PRIOR department_num = parent_department_num
)
ORDER BY e.employee_num DESC )
WHERE ROWNUM <= #{end}
) WHERE rn >= #{start}

SELECT CONTRACT_NUM FROM (
    SELECT CONTRACT_NUM FROM HR_EMPLOYEE_CONTRACT 
    WHERE EMPLOYEE_NUM = #{employeeNum}
    ORDER BY REGISTER_DATE DESC
) WHERE ROWNUM = 1
